import React, { Component } from 'react';

class GetAccessToken extends Component{
    constructor(props){
        super(props);
        console.log("GetAccessToken")
    }

    componentDidUpdate(prevProps, prevState, snapshot){
        if(prevProps.kakaocode !== this.props.kakaocode){
            let api_url = process.env.REACT_APP_STOCK_API_URL+"/GetKakaoAccessToken?kakaocode="+this.props.kakaocode;
            fetch(api_url)
                .then(res => res.json())
                .then(data =>{
                    console.log("dartlist didupdate fetch", data);
                    this.setState({columns : [{title:"날짜", field:"rcept_dt"}, 
                                              {title:"보고서명", field:"report_nm"}, 
                                              {title:"보고서번호", field:"rcept_no", render:data => <a href={'http://dart.fss.or.kr/dsaf001/main.do?rcpNo='+data.rcept_no} target='_blank'>{data.rcept_no}</a>},
                                              {title:"공시제출인명", field:"flr_nm"},
                                              {title:"비고", field:"rm"}]})
                    this.setState({data:data["dart_list"]});
                });
        }
    }
    render(){
        return  (
            <div>

            </div>
         );
    }
}

export default GetAccessToken;
