import React from 'react'; 
import { Card, CardActions, CardContent, Typography, Button } from '@material-ui/core';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import ListIcon from '@material-ui/icons/List';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import CodeInfo from './CodeInfo';
import OrderInfo from './OrderInfo';
//import WordSearch from '../components/WordSearch';

const useStyles = {
  root: {
    minWidth: 275,
	maxWidth: 300
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  bottom:{
   width: "100%",
   position: "fixed",
   bottom: "10",
  }
};

function fetchAPI(param) {
  // param is a highlighted word from the user before it clicked the button
  return fetch("https://kauth.kakao.com/oauth/authorize?client_id=028ea5b683384907ee2126203f9e033d&redirect_uri=https://blackas.github.io/testreactweb/kakaoauth");
}

class Navigation extends React.Component {
    constructor(props){
        super(props);
        this.state={
            value:0,
			userinput:'',
			result:[],
        }
    }

    handleChange = (event, value) => {
        this.setState({ value });
    };

	handleUserInput = (e) => {
		this.setState({ userinput:e.target.value });
	};

	toggleButtonState = () => {
        fetchAPI(this.state.userinput).then(result => {
            this.setState({ result });
        });
    };

    render() {
        const { value } = this.state.value;
        return (
            <div className={useStyles.root}>
                <div>
                    {this.state.value === 0?
                        (<CodeInfo/>) : (<OrderInfo/>)
                    }
                </div>
				<Card className={useStyles.root}>
				  <CardContent>
					<Typography component="h2">
					  <input type="search" placeholder="검색어를 입력해주세요." onChange={this.handleUserInput}/>
					</Typography>
				  </CardContent>
				  <CardActions>
					<Button size="small" onClick={this.toggleButtonState}>검색</Button>
				  </CardActions>
				</Card>
                <BottomNavigation value={this.state.value} onChange={this.handleChange} 
                    showLabels className={useStyles.bottom}>
                    <BottomNavigationAction label="종목정보" value={0} icon={<ListIcon />} />
                    <BottomNavigationAction label="매매이력" value={1} icon={<ShoppingCartIcon/>} />
                </BottomNavigation>
            </div>
        );
    }
}

export default Navigation;
