import React from 'react'; 
import { Card, CardActions, CardContent, Typography, Button } from '@material-ui/core';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import ListIcon from '@material-ui/icons/List';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import CodeInfo from './CodeInfo';
import Login from './Login';

import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import DraftsIcon from '@material-ui/icons/Drafts';
import SendIcon from '@material-ui/icons/Send';

//import WordSearch from '../components/WordSearch';

const useStyles = {
  root: {
    minWidth: 275,
	maxWidth: 300
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  bottom:{
   width: "100%",
   position: "fixed",
   bottom: "10",
 }
};

function fetchAPI(param) {
  // param is a highlighted word from the user before it clicked the button
  return fetch("http://api.seibro.or.kr/openapi/service/FnTermSvc/getFinancialTermMeaning?numOfRows=5&pageNo=1&ServiceKey=fjGWMMABZnp7n8Wg67jbCDcry6FAX4qXzjLFhZ4r37WEoOjnrlrnDogxE7HPexyDmcJveyyy7%2F75nue9VbpL%2Fw%3D%3D&term=" + param);
}

class Navigation extends React.Component {
    constructor(props){
        super(props);
        this.state={
            value:0,
			userinput:'',
			result:[],
        }
    }

    handleChange = (event, value) => {
        this.setState({ value });
    };

	handleUserInput = (e) => {
		this.setState({ userinput:e.target.value });
	};

	toggleButtonState = () => {
        fetchAPI(this.state.userinput).then(result => {
            this.setState({ result });
        });
    };

    render() {
        const { value } = this.state.value;
        const [anchorEl, setAnchorEl] = React.useState(null);

        const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
        };

        const handleClose = () => {
        setAnchorEl(null);
        };
        return (
            <div>
                <Button
                    aria-controls="customized-menu"
                    aria-haspopup="true"
                    variant="contained"
                    color="primary"
                    onClick={handleClick}
                    >
                    Open Menu
                </Button>
                <StyledMenu
                    id="customized-menu"
                    anchorEl={anchorEl}
                    keepMounted
                    open={Boolean(anchorEl)}
                    onClose={handleClose}
                >
                <StyledMenuItem>
                    <ListItemIcon>
                        <SendIcon fontSize="small" />
                    </ListItemIcon>
                    <ListItemText primary="Sent mail" />
                </StyledMenuItem>
                <StyledMenuItem>
                    <ListItemIcon>
                        <DraftsIcon fontSize="small" />
                    </ListItemIcon>
                    <ListItemText primary="Drafts" />
                </StyledMenuItem>
                </StyledMenu>
                <div className={useStyles.root}>
                    <div>
                        {this.state.value === 0?
                            (<CodeInfo/>) : (<CodeInfo/>)
                        }
                    </div>
                    <Card className={useStyles.root}>
                        <CardContent>
                            <Typography component="h2">
                            <input type="search" placeholder="검색어를 입력해주세요." onChange={this.handleUserInput}/>
                            </Typography>
                        </CardContent>
                        <CardActions>
                            <Button size="small" onClick={this.toggleButtonState}>검색</Button>
                        </CardActions>
                    </Card>
                    <BottomNavigation value={this.state.value} onChange={this.handleChange} 
                        showLabels className={useStyles.bottom}>
                        <BottomNavigationAction label="종목정보" value={0} icon={<ListIcon />} />
                        <BottomNavigationAction label="매매이력" value={1} icon={<ShoppingCartIcon/>} />
                    </BottomNavigation>
                </div>
            </div>
        );
    }
}

export default Navigation;
